const API_endpoint = `api.openweathermap.org/data/2.5/forecast?`; 
const appId = `079fae9831c9aea9535a5467f47e1a46`



useEffect(() => {
  navigator.geolocation.getCurrentPosition((position)=>{
    setLatitude(position.coords.latitude);
    setLongitude(position.coords.longitude)
  })
  async function infoClima(){ await fetch(`https://api.openweathermap.org/data/2.5/forecast?lat=${latitude}&lon=${longitude}&appid=${appId}`)
  .then( response => response.json())
  .then(jsonResponse =>setClima(jsonResponse))
  .catch(err =>{console.log(err)})
  }
